import React, { Component } from 'react';


class SpnuNotice extends Component {
    state = {
        Boards : {}

    };

    async componentDidMount(){
        this._getBoards();
        
    }

    _renderBoard = () => {
        const Boards = this.state.Boards.map(Board => {
            return 
            <div key={Board.id}> 
            <h1>{Board.title}</h1>
            <span>{Board.author}</span>
            <span>{Board.text}</span>
            <span>{Board.created_date}</span>
            <span>{Board.hits}</span>
            </div>
        })
        return Boards

    }


    _getBoards = async () => {
        const Boards = await this._callApi()
        this.setState({
          Boards
        })
      }

    _callApi = () =>{
    return fetch('http://127.0.0.1:8000/SPNU_notice/')
    .then(response => response.json()) //response는 변수로 아무거나 가능  //콘솔에 뜨는 ReadableStream은 010101처럼 데이터를 받는다는거고, 이걸 json으로 변환시켜야한다.
    .then(json => json.data.Boards)
    .catch(err => console.log(err))          //원래는 catch(function(err){}) 가능
    }


    render(){
        return(
            <div>
                <h1> hello world </h1>
                {this.state.Boards ? this._renderBoard() : "loading"}
            </div>
        )
    }

}

export default SpnuNotice;
