    };

    async componentDidMount(){
        this._getBoards();
        
    }

    _getBoards = async () => {
        const Boards = await this._callApi()
        this.setState({
          Boards
        })
      }

    _callApi = () =>{
    return fetch("https://yts.lt/api/v2/list_movies.json?sort_by=like_count")
